import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useStore } from '../contexts/StoreContext';
import { ShoppingCart, Plus, Minus, ArrowLeft, Store, Package, Edit, Video, Play, Copy } from 'lucide-react';

export default function StoreView() {
  const { storeId } = useParams();
  const { stores, products, addToCart, cart } = useStore();
  const { currentUser, userType } = useAuth();
  const navigate = useNavigate();

  // Debug: Log store ID and available stores
  console.log('Looking for store ID:', storeId);
  console.log('Available stores:', stores);

  const store = stores.find(s => s.id === storeId);
  console.log('Found store:', store);

  const storeProducts = products.filter(p => p.storeId === storeId);
  const [quantities, setQuantities] = useState({});
  const [selectedProduct, setSelectedProduct] = useState(null);

  // Check if current user is the owner of this store
  const isStoreOwner = store && currentUser && store.ownerId === currentUser.uid;
  const canBuy = !isStoreOwner && (!currentUser || userType === 'buyer');

  // Debug: Log user role and permissions
  console.log('User permissions:', {
    currentUser: currentUser?.email,
    userType,
    isStoreOwner,
    canBuy,
    storeOwnerId: store?.ownerId
  });

  const copyStoreLink = () => {
    const currentUrl = window.location.href;
    const baseUrl = currentUrl.split('/store/')[0];
    const link = `${baseUrl}/store/${storeId}`;
    navigator.clipboard.writeText(link);
    alert('Store link copied to clipboard!');
  };

  if (!store) {
    return (
      <div className="min-h-screen bg-gray-50 p-4">
        <div className="text-center py-12">
          <Store className="w-16 h-16 text-gray-300 mx-auto mb-4" />
          <h2 className="text-xl font-semibold text-gray-900 mb-2">Store Not Found</h2>
          <p className="text-gray-600 mb-6">This store doesn't exist or has been removed</p>
          <button
            onClick={() => navigate('/')}
            className="bg-primary text-white px-6 py-2 rounded-lg font-medium hover:bg-blue-600 transition-colors"
          >
            Go Home
          </button>
        </div>
      </div>
    );
  }

  const handleAddToCart = (product) => {
    const quantity = quantities[product.id] || 1;
    for (let i = 0; i < quantity; i++) {
      addToCart(product);
    }
    setQuantities({...quantities, [product.id]: 1});
  };

  const updateQuantity = (productId, change) => {
    setQuantities(prev => ({
      ...prev,
      [productId]: Math.max(1, (prev[productId] || 1) + change)
    }));
  };

  const cartItemCount = cart.reduce((sum, item) => sum + item.quantity, 0);

  return (
    <div className="min-h-screen bg-white text-black">
      {/* Store Header */}
      <div 
        className="text-white p-4"
        style={{ backgroundColor: store.primaryColor || '#9333ea' }}
      >
        <button
          onClick={() => navigate(-1)}
          className="flex items-center text-white/80 hover:text-white mb-4"
        >
          <ArrowLeft className="w-4 h-4 mr-2" />
          Back
        </button>

        <div className="flex items-start space-x-4">
          {/* Store Logo - Left Side */}
          <div className="flex-shrink-0">
            {store.logo ? (
              <img
                src={store.logo}
                alt={store.name}
                className="w-16 h-16 object-cover rounded-xl border-2 border-white/30"
              />
            ) : (
              <div className="w-16 h-16 bg-white/20 rounded-xl flex items-center justify-center">
                <Store className="w-8 h-8 text-white" />
              </div>
            )}
          </div>

          {/* Store Info - Right Side */}
          <div className="flex-1 text-left">
            <h1 className="text-xl font-bold mb-2">{store.name}</h1>
            <p className="text-white/90 text-sm mb-3 line-clamp-2">{store.description}</p>
            <div className="flex flex-wrap items-center gap-2 text-xs">
              <span className="bg-white/20 px-2 py-1 rounded-full">
                {store.category}
              </span>
              <span className="text-white/80">
                {storeProducts.length} products
              </span>
              {isStoreOwner && (
                <span className="bg-yellow-400 text-yellow-900 px-2 py-1 rounded-full font-medium">
                  Your Store
                </span>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Copy Store Link */}
      <div className="bg-gray-50 border-b border-gray-200 p-3">
        <div className="flex items-center justify-between">
          <div>
            <p className="text-sm font-medium text-black">Share this store</p>
            <p className="text-xs text-gray-600">Copy link to share with customers</p>
          </div>
          <button
            onClick={copyStoreLink}
            className="text-white p-2 rounded-lg hover:opacity-90 transition-opacity flex items-center"
            style={{ backgroundColor: store.secondaryColor || '#a855f7' }}
          >
            <Copy className="w-3 h-3 mr-1" />
            <span className="text-xs">Copy Link</span>
          </button>
        </div>
      </div>

      {/* Owner Actions */}
      {isStoreOwner && (
        <div className="bg-yellow-50 border-b border-yellow-200 p-4">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-yellow-800">Store Owner View</p>
              <p className="text-xs text-yellow-600">You're viewing your own store</p>
            </div>
            <button
              onClick={() => navigate('/manage-products')}
              className="bg-yellow-600 text-white px-3 py-1 rounded-lg text-sm font-medium hover:bg-yellow-700 transition-colors flex items-center"
            >
              <Edit className="w-4 h-4 mr-1" />
              Manage Products
            </button>
          </div>
        </div>
      )}

      {/* Cart Button for Buyers */}
      {canBuy && cartItemCount > 0 && (
        <div className="sticky top-0 z-10 bg-white border-b border-gray-200 p-4">
          <button
            onClick={() => navigate('/checkout')}
            className="w-full bg-secondary text-white py-3 px-4 rounded-lg font-medium hover:bg-green-600 transition-colors flex items-center justify-center"
          >
            <ShoppingCart className="w-5 h-5 mr-2" />
            View Cart ({cartItemCount} items)
          </button>
        </div>
      )}

      {/* Products */}
      <div className="p-4">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-lg font-semibold text-gray-900">Products</h2>
          <div className="flex items-center space-x-2 text-sm text-gray-500">
            <span>Layout: {store.layout || 'grid'}</span>
          </div>
        </div>

        {storeProducts.length === 0 ? (
          <div className="text-center py-12">
            <Package className="w-12 h-12 text-gray-300 mx-auto mb-3" />
            <p className="text-gray-500">No products available yet</p>
            {isStoreOwner && (
              <button
                onClick={() => navigate('/manage-products')}
                className="mt-4 bg-primary text-white px-4 py-2 rounded-lg text-sm font-medium hover:bg-blue-600 transition-colors"
              >
                Add Products
              </button>
            )}
          </div>
        ) : (
          <div className={
            store.layout === 'list' 
              ? 'space-y-4' 
              : 'grid grid-cols-1 gap-4'
          }>
            {storeProducts.map(product => (
              <div key={product.id} className="bg-white rounded-lg shadow-sm overflow-hidden">
                {store.layout === 'list' ? (
                  /* List Layout - Professional E-commerce Style */
                  <div className="bg-white rounded-lg shadow-sm hover:shadow-md transition-shadow duration-200 overflow-hidden">
                    <div className="flex">
                      {/* Product Images */}
                      <div className="flex-shrink-0 w-48">
                        {product.images && product.images.length > 0 ? (
                          <div className="relative">
                            <img
                              src={product.images[0]}
                              alt={product.name}
                              className="w-48 h-48 object-cover"
                            />
                            {product.images.length > 1 && (
                              <div className="absolute bottom-2 right-2 bg-black/70 text-white text-xs px-2 py-1 rounded">
                                +{product.images.length - 1}
                              </div>
                            )}
                            {product.video && (
                              <div className="absolute top-2 left-2 bg-red-500 text-white px-2 py-1 rounded text-xs font-medium">
                                Video
                              </div>
                            )}
                          </div>
                        ) : (
                          <div className="w-48 h-48 bg-gray-200 flex items-center justify-center">
                            <Package className="w-12 h-12 text-gray-400" />
                          </div>
                        )}
                      </div>

                      <div className="flex-1 p-4">
                        <div className="flex justify-between items-start mb-2">
                          <div className="flex-1">
                            {/* Product Name */}
                            <h3 className="font-semibold text-gray-900 mb-1 hover:text-blue-600 transition-colors cursor-pointer">
                              {product.name}
                            </h3>

                            {/* Rating */}
                            <div className="flex items-center mb-2">
                              <div className="flex text-yellow-400">
                                {[...Array(5)].map((_, i) => (
                                  <svg key={i} className="w-4 h-4 fill-current" viewBox="0 0 20 20">
                                    <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z"/>
                                  </svg>
                                ))}
                              </div>
                              <span className="text-sm text-gray-600 ml-1">4.5</span>
                              <span className="text-sm text-gray-500 ml-1">(234)</span>
                            </div>

                            {/* Description */}
                            <p className="text-sm text-gray-600 mb-2 line-clamp-2">
                              {product.description || 'No description available'}
                            </p>

                            {/* Features */}
                            <div className="flex flex-wrap gap-2 mb-2">
                              <span className="text-xs bg-blue-100 text-blue-800 px-2 py-1 rounded">Free Shipping</span>
                              <span className="text-xs bg-green-100 text-green-800 px-2 py-1 rounded">In Stock</span>
                              <span className="text-xs bg-purple-100 text-purple-800 px-2 py-1 rounded">Fast Delivery</span>
                            </div>
                          </div>

                          {/* Prime Badge */}
                          <div className="bg-blue-600 text-white px-3 py-1 rounded text-xs font-medium">
                            Prime
                          </div>
                        </div>
                      </div>

                      <div className="border-t border-gray-100 p-4">
                        <div className="flex justify-between items-end">
                          <div>
                            {/* Price */}
                            <div className="flex items-baseline mb-1">
                              <span className="text-2xl font-bold text-red-600">${product.price}</span>
                              <span className="text-sm text-gray-500 line-through ml-2">$99.99</span>
                            </div>

                            {/* Stock Status */}
                            {product.stock !== undefined && product.stock > 0 && product.stock < 10 && (
                              <p className="text-xs text-orange-600">Only {product.stock} left in stock</p>
                            )}
                            {product.stock !== undefined && product.stock >= 10 && (
                              <p className="text-xs text-green-600">In Stock - Ships within 24h</p>
                            )}
                            {product.stock === 0 && (
                              <p className="text-xs text-red-600">Out of Stock</p>
                            )}
                          </div>

                          {/* Action Buttons */}
                          <div className="flex flex-col space-y-2">
                            {canBuy && (product.stock === undefined || product.stock > 0) ? (
                              <>
                                <div className="flex items-center space-x-2">
                                  <div className="flex items-center border border-gray-300 rounded-lg">
                                    <button
                                      onClick={() => updateQuantity(product.id, -1)}
                                      className="p-2 hover:bg-gray-100 transition-colors"
                                    >
                                      <Minus className="w-4 h-4" />
                                    </button>
                                    <input
                                      type="number"
                                      value={quantities[product.id] || 1}
                                      onChange={(e) => {
                                        const val = parseInt(e.target.value) || 1;
                                        setQuantities(prev => ({
                                          ...prev,
                                          [product.id]: Math.max(1, val)
                                        }));
                                      }}
                                      className="w-12 text-center border-0 focus:outline-none"
                                      min="1"
                                    />
                                    <button
                                      onClick={() => updateQuantity(product.id, 1)}
                                      className="p-2 hover:bg-gray-100 transition-colors"
                                    >
                                      <Plus className="w-4 h-4" />
                                    </button>
                                  </div>

                                  <button
                                    onClick={() => handleAddToCart(product)}
                                    className="text-white py-2 px-6 rounded-lg font-medium hover:opacity-90 transition-opacity flex items-center"
                                    style={{ backgroundColor: store.secondaryColor || '#f59e0b' }}
                                  >
                                    <ShoppingCart className="w-4 h-4 mr-2" />
                                    Add to Cart
                                  </button>
                                </div>

                                <button className="text-gray-600 border border-gray-300 py-1 px-3 rounded text-sm hover:bg-gray-50 transition-colors">
                                   Add to Wishlist
                                </button>
                              </>
                            ) : isStoreOwner ? (
                              <div className="bg-gray-100 text-gray-600 py-2 px-4 rounded-lg text-sm font-medium text-center">
                                Your Product
                              </div>
                            ) : (
                              <button
                                onClick={() => handleAddToCart(product)}
                                disabled={product.stock === 0}
                                className="text-white py-2 px-4 rounded-lg text-sm font-medium hover:opacity-90 transition-opacity flex items-center justify-center"
                                style={{ backgroundColor: store.secondaryColor || '#a855f7' }}
                              >
                                <ShoppingCart className="w-3 h-3 mr-1" />
                                Add to Cart
                              </button>
                            )}
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                ) : (
                  /* Grid Layout - Amazon/Alibaba Style */
                  <div className="group hover:shadow-lg transition-shadow duration-200">
                    {/* Product Image */}
                    <div className="relative overflow-hidden bg-gray-100">
                      {product.images && product.images.length > 0 ? (
                        <img
                          src={product.images[0]}
                          alt={product.name}
                          className="w-full h-48 object-cover group-hover:scale-105 transition-transform duration-200"
                        />
                      ) : (
                        <div className="w-full h-48 bg-gray-200 flex items-center justify-center">
                          <Package className="w-12 h-12 text-gray-400" />
                        </div>
                      )}

                      {/* Media badges */}
                      <div className="absolute top-2 left-2 flex flex-col space-y-1">
                        {product.video && (
                          <div className="bg-red-500 text-white px-2 py-1 rounded text-xs font-medium">
                            Video
                          </div>
                        )}
                        {product.images && product.images.length > 1 && (
                          <div className="bg-black/70 text-white px-2 py-1 rounded text-xs font-medium">
                            +{product.images.length - 1} Photos
                          </div>
                        )}
                      </div>

                      {/* Hover overlay */}
                      <div className="absolute inset-0 bg-black/5 opacity-0 group-hover:opacity-100 transition-opacity duration-200" />
                    </div>

                    <div className="p-4">
                      {/* Product Name */}
                      <h3 className="font-medium text-gray-900 mb-2 line-clamp-2 hover:text-blue-600 transition-colors cursor-pointer">
                        {product.name}
                      </h3>

                      {/* Product Description */}
                      <p className="text-sm text-gray-600 mb-3 line-clamp-2">
                        {product.description || 'No description available'}
                      </p>

                      {/* Rating and Reviews */}
                      <div className="flex items-center mb-3">
                        <div className="flex text-yellow-400">
                          {[...Array(5)].map((_, i) => (
                            <svg key={i} className="w-4 h-4 fill-current" viewBox="0 0 20 20">
                              <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z"/>
                            </svg>
                          ))}
                        </div>
                        <span className="text-sm text-gray-600 ml-1">4.5</span>
                        <span className="text-sm text-gray-500 ml-1">(234)</span>
                      </div>

                      {/* Price Section */}
                      <div className="flex items-end justify-between mb-3">
                        <div>
                          <div className="flex items-baseline">
                            <span className="text-2xl font-bold text-red-600">${product.price}</span>
                            {product.stock !== undefined && product.stock > 0 && product.stock < 10 && (
                              <span className="text-xs text-orange-600 ml-2">Only {product.stock} left!</span>
                            )}
                          </div>
                          {product.stock !== undefined && product.stock > 10 && (
                            <p className="text-sm text-green-600">In Stock</p>
                          )}
                          {product.stock === 0 && (
                            <p className="text-sm text-red-600">Out of Stock</p>
                          )}
                        </div>
                      </div>

                      {/* Prime Badge */}
                      <div className="flex items-center mb-3">
                        <div className="bg-blue-600 text-white px-2 py-1 rounded text-xs font-medium">
                          Prime
                        </div>
                        <span className="text-xs text-gray-600 ml-2">FREE Delivery</span>
                      </div>

                      {/* Action Buttons */}
                      <div className="flex space-x-2">
                        {canBuy && (product.stock === undefined || product.stock > 0) ? (
                          <>
                            <div className="flex items-center border border-gray-300 rounded-lg flex-1">
                              <button
                                onClick={() => updateQuantity(product.id, -1)}
                                className="p-2 hover:bg-gray-100 transition-colors"
                              >
                                <Minus className="w-4 h-4" />
                              </button>
                              <input
                                type="number"
                                value={quantities[product.id] || 1}
                                onChange={(e) => {
                                  const val = parseInt(e.target.value) || 1;
                                  setQuantities(prev => ({
                                    ...prev,
                                    [product.id]: Math.max(1, val)
                                  }));
                                }}
                                className="w-12 text-center border-0 focus:outline-none"
                                min="1"
                              />
                              <button
                                onClick={() => updateQuantity(product.id, 1)}
                                className="p-2 hover:bg-gray-100 transition-colors"
                              >
                                <Plus className="w-4 h-4" />
                              </button>
                            </div>

                            <button
                              onClick={() => handleAddToCart(product)}
                              className="flex-1 text-white py-2 px-4 rounded-lg font-medium hover:opacity-90 transition-opacity flex items-center justify-center"
                              style={{ backgroundColor: store.secondaryColor || '#f59e0b' }}
                            >
                              <ShoppingCart className="w-4 h-4 mr-2" />
                              Add to Cart
                            </button>
                          </>
                        ) : isStoreOwner ? (
                          <div className="flex-1 bg-gray-100 text-gray-600 py-2 px-4 rounded-lg text-sm font-medium text-center">
                            Your Product
                          </div>
                        ) : (
                          <button
                            disabled
                            className="flex-1 bg-gray-100 text-gray-400 py-2 px-4 rounded-lg text-sm font-medium cursor-not-allowed"
                          >
                            {product.stock === 0 ? 'Out of Stock' : 'Login to Buy'}
                          </button>
                        )}
                      </div>

                      {/* Additional Info */}
                      <div className="mt-3 pt-3 border-t border-gray-100">
                        <div className="flex items-center justify-between text-xs text-gray-500">
                          <span>Free shipping</span>
                          <span>30-day returns</span>
                        </div>
                      </div>
                    </div>
                  </div>
                )}

                {/* Product Video Preview */}
                {product.video && (
                  <div className="border-t border-gray-100 p-4">
                    <div className="flex items-center justify-between mb-2">
                      <span className="text-sm font-medium text-gray-700">Product Video</span>
                      <button
                        onClick={() => setSelectedProduct(selectedProduct === product.id ? null : product.id)}
                        className="text-primary hover:bg-blue-50 p-1 rounded transition-colors"
                      >
                        {selectedProduct === product.id ? 'Hide' : <Play className="w-4 h-4" />}
                      </button>
                    </div>

                    {selectedProduct === product.id && (
                      <div className="space-y-2">
                        <video
                          src={product.video}
                          controls
                          className="w-full h-48 object-cover rounded-lg"
                        />
                        <p className="text-xs text-gray-500">Product video demonstration</p>
                      </div>
                    )}
                  </div>
                )}

                {/* Product Images Gallery */}
                {product.images && product.images.length > 1 && (
                  <div className="border-t border-gray-100 p-4">
                    <p className="text-sm font-medium text-gray-700 mb-2">More Images</p>
                    <div className="grid grid-cols-4 gap-2">
                      {product.images.slice(1).map((image, index) => (
                        <img
                          key={index}
                          src={image}
                          alt={`${product.name} ${index + 2}`}
                          className="w-full h-20 object-cover rounded cursor-pointer hover:opacity-80 transition-opacity"
                        />
                      ))}
                    </div>
                  </div>
                )}
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
