import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useStore } from '../contexts/StoreContext';
import { Home, Store, ShoppingCart, User, Plus } from 'lucide-react';

export default function Navigation() {
  const { currentUser, userType } = useAuth();
  const { cart } = useStore();
  const location = useLocation();

  const cartItemCount = cart.reduce((sum, item) => sum + item.quantity, 0);

  // Don't show navigation on auth pages
  if (['/login', '/register'].includes(location.pathname)) {
    return null;
  }

  return (
    <nav className="bg-black border-t border-gray-800 px-4 py-2">
      <div className="flex justify-around items-center">
        <Link
          to="/"
          className={`flex flex-col items-center p-2 rounded-lg transition-all ${
            location.pathname === '/' ? 'text-purple-500' : 'text-gray-400 hover:text-white'
          }`}
        >
          <Home className="w-4 h-4" />
          <span className="text-xs mt-1">Home</span>
        </Link>

        {currentUser && userType === 'seller' && (
          <>
            <Link
              to="/seller-dashboard"
              className={`flex flex-col items-center p-2 rounded-lg transition-all ${
                location.pathname.includes('seller') ? 'text-purple-500' : 'text-gray-400 hover:text-white'
              }`}
            >
              <Store className="w-4 h-4" />
              <span className="text-xs mt-1">Store</span>
            </Link>

            <Link
              to="/create-store"
              className="flex flex-col items-center p-2 rounded-lg transition-all text-gray-400 hover:text-white"
            >
              <Plus className="w-4 h-4" />
              <span className="text-xs mt-1">New</span>
            </Link>
          </>
        )}

        {currentUser && userType === 'buyer' && (
          <>
            <Link
              to="/buyer-dashboard"
              className={`flex flex-col items-center p-2 rounded-lg transition-all ${
                location.pathname.includes('buyer') ? 'text-purple-500' : 'text-gray-400 hover:text-white'
              }`}
            >
              <ShoppingCart className="w-4 h-4" />
              <span className="text-xs mt-1">Orders</span>
            </Link>

            {cartItemCount > 0 && (
              <Link
                to="/checkout"
                className="relative flex flex-col items-center p-2 rounded-lg transition-all text-gray-400 hover:text-white"
              >
                <ShoppingCart className="w-4 h-4" />
                <span className="text-xs mt-1">Cart</span>
                <span className="absolute -top-1 -right-1 bg-purple-500 text-white text-xs rounded-full w-4 h-4 flex items-center justify-center text-[10px]">
                  {cartItemCount}
                </span>
              </Link>
            )}
          </>
        )}

        {!currentUser && (
          <Link
            to="/login"
            className="flex flex-col items-center p-2 rounded-lg transition-all text-gray-400 hover:text-white"
          >
            <User className="w-4 h-4" />
            <span className="text-xs mt-1">Login</span>
          </Link>
        )}
      </div>
    </nav>
  );
}
