import React, { createContext, useContext, useState, useEffect } from 'react';

const AuthContext = createContext();

export function useAuth() {
  return useContext(AuthContext);
}

export function AuthProvider({ children }) {
  const [currentUser, setCurrentUser] = useState(null);
  const [userType, setUserType] = useState(null);
  const [loading, setLoading] = useState(true);

  // Mock authentication functions for demo
  function signup(email, password, type) {
    return new Promise((resolve, reject) => {
      setTimeout(() => {
        // Create mock user
        const mockUser = {
          uid: 'user_' + Date.now(),
          email: email,
          displayName: email.split('@')[0]
        };

        setCurrentUser(mockUser);
        setUserType(type);
        localStorage.setItem('currentUser', JSON.stringify(mockUser));
        localStorage.setItem('userType', type);

        resolve({ user: mockUser });
      }, 1000);
    });
  }

  function login(email, password, type) {
    return new Promise((resolve, reject) => {
      setTimeout(() => {
        // Mock login - accept any email/password for demo
        const mockUser = {
          uid: 'user_' + Date.now(),
          email: email,
          displayName: email.split('@')[0]
        };

        setCurrentUser(mockUser);
        setUserType(type);
        localStorage.setItem('currentUser', JSON.stringify(mockUser));
        localStorage.setItem('userType', type);

        resolve({ user: mockUser });
      }, 1000);
    });
  }

  function logout() {
    setCurrentUser(null);
    setUserType(null);
    localStorage.removeItem('currentUser');
    localStorage.removeItem('userType');
    return Promise.resolve();
  }

  function updateUserType(type) {
    setUserType(type);
    localStorage.setItem('userType', type);
  }

  useEffect(() => {
    // Check for existing session on load
    const savedUser = localStorage.getItem('currentUser');
    const savedUserType = localStorage.getItem('userType');

    console.log('=== AUTH DEBUG ===');
    console.log('savedUser from localStorage:', savedUser);
    console.log('savedUserType from localStorage:', savedUserType);

    if (savedUser && savedUserType) {
      try {
        const parsedUser = JSON.parse(savedUser);
        console.log('Parsed user:', parsedUser);
        setCurrentUser(parsedUser);
        setUserType(savedUserType);
      } catch (error) {
        console.error('Error parsing saved user:', error);
        localStorage.removeItem('currentUser');
        localStorage.removeItem('userType');
      }
    } else {
      console.log('No saved user found - user is not logged in');
    }

    setLoading(false);
  }, []);

  const value = {
    currentUser,
    userType,
    signup,
    login,
    logout,
    updateUserType
  };

  return (
    <AuthContext.Provider value={value}>
      {!loading && children}
    </AuthContext.Provider>
  );
}
