import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useStore } from '../contexts/StoreContext';
import { Home, Store, ShoppingCart, User, Plus } from 'lucide-react';

export default function Navigation() {
  const { currentUser } = useAuth();
  const { cart } = useStore();
  const location = useLocation();

  const cartItemCount = cart.reduce((sum, item) => sum + item.quantity, 0);

  // Don't show navigation on auth pages
  if (['/login', '/register'].includes(location.pathname)) {
    return null;
  }

  return (
    <nav className="bg-white border-t border-gray-200 px-4 py-2">
      <div className="flex justify-around items-center">
        <Link
          to="/"
          className={`flex flex-col items-center p-2 rounded-lg transition-colors ${
            location.pathname === '/' ? 'text-primary' : 'text-gray-600 hover:text-gray-900'
          }`}
        >
          <Home className="w-5 h-5" />
          <span className="text-xs mt-1">Home</span>
        </Link>

        {currentUser && (
          <>
            <Link
              to="/seller-dashboard"
              className={`flex flex-col items-center p-2 rounded-lg transition-colors ${
                location.pathname.includes('seller') ? 'text-primary' : 'text-gray-600 hover:text-gray-900'
              }`}
            >
              <Store className="w-5 h-5" />
              <span className="text-xs mt-1">Sell</span>
            </Link>

            <Link
              to="/buyer-dashboard"
              className={`flex flex-col items-center p-2 rounded-lg transition-colors ${
                location.pathname.includes('buyer') ? 'text-primary' : 'text-gray-600 hover:text-gray-900'
              }`}
            >
              <ShoppingCart className="w-5 h-5" />
              <span className="text-xs mt-1">Buy</span>
            </Link>

            <Link
              to="/create-store"
              className="flex flex-col items-center p-2 rounded-lg transition-colors text-gray-600 hover:text-gray-900"
            >
              <Plus className="w-5 h-5" />
              <span className="text-xs mt-1">New</span>
            </Link>
          </>
        )}

        {!currentUser && (
          <Link
            to="/login"
            className="flex flex-col items-center p-2 rounded-lg transition-colors text-gray-600 hover:text-gray-900"
          >
            <User className="w-5 h-5" />
            <span className="text-xs mt-1">Login</span>
          </Link>
        )}

        {currentUser && cartItemCount > 0 && (
          <Link
            to="/checkout"
            className="relative flex flex-col items-center p-2 rounded-lg transition-colors text-gray-600 hover:text-gray-900"
          >
            <ShoppingCart className="w-5 h-5" />
            <span className="text-xs mt-1">Cart</span>
            <span className="absolute -top-1 -right-1 bg-red-500 text-white text-xs rounded-full w-5 h-5 flex items-center justify-center">
              {cartItemCount}
            </span>
          </Link>
        )}
      </div>
    </nav>
  );
}
