import React, { useState, useMemo } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useStore } from '../contexts/StoreContext';
import {
  ShoppingCart, Plus, Minus, ArrowLeft, Store, Package,
  Edit, Copy, Shield, Star, Truck, Heart, ChevronRight,
  Tag, CheckCircle, Zap
} from 'lucide-react';

export default function StoreView() {
  const { storeId } = useParams();
  const location = useLocation();
  const { stores, products, addToCart, cart } = useStore();
  const { currentUser } = useAuth();
  const navigate = useNavigate();

  // --- Decode URL-embedded store data (for buyers on other devices) ---
  const urlData = useMemo(() => {
    try {
      const params = new URLSearchParams(location.search);
      const encoded = params.get('d');
      if (encoded) {
        const decoded = JSON.parse(decodeURIComponent(escape(atob(encoded))));
        return decoded;
      }
    } catch (e) {
      console.warn('Could not decode URL store data:', e);
    }
    return null;
  }, [location.search]);

  // Use URL-embedded data first (buyer), then localStorage (owner direct view)
  const store = urlData?.store || stores.find(s => s.id === storeId);

  // For logo: if URL-data stripped it, try to find the original from localStorage
  const localStore = stores.find(s => s.id === storeId);
  const storeLogo = store?.logo || localStore?.logo || null;

  const storeProducts = urlData?.products || products.filter(p => p.storeId === storeId);

  const [quantities, setQuantities] = useState({});
  const [addedToCart, setAddedToCart] = useState({});
  const [wishlist, setWishlist] = useState({});

  // isStoreOwner only when logged-in uid matches AND no url-encoded payload
  const isStoreOwner = !urlData && store && currentUser && store.ownerId === currentUser.uid;
  const canBuy = !isStoreOwner;

  // Build shareable link (strips base64 blobs to keep URL short)
  const buildShareableLink = () => {
    const base = window.location.origin;
    const ownerProducts = products.filter(p => p.storeId === storeId);
    const safeStore = { ...store, logo: null };
    const safeProducts = ownerProducts.map(p => ({
      ...p,
      images: (p.images || []).filter(img => !img.startsWith('data:')),
      video: p.video && !p.video.startsWith('data:') ? p.video : null,
    }));
    const payload = { store: safeStore, products: safeProducts };
    const encoded = btoa(unescape(encodeURIComponent(JSON.stringify(payload))));
    return `${base}/store/${storeId}?d=${encoded}`;
  };

  const copyStoreLink = () => {
    const link = buildShareableLink();
    navigator.clipboard.writeText(link);
    alert('Store link copied! Share this with your buyers.');
  };

  if (!store) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center p-4">
        <div className="text-center">
          <div className="w-20 h-20 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-4">
            <Store className="w-10 h-10 text-gray-300" />
          </div>
          <h2 className="text-xl font-semibold text-gray-900 mb-2">Store Not Found</h2>
          <p className="text-gray-500 mb-6 text-sm">This store doesn't exist or the link has expired.</p>
          <button
            onClick={() => navigate('/')}
            className="bg-primary text-white px-6 py-3 rounded-xl font-semibold hover:opacity-90 transition-all"
          >
            Go Home
          </button>
        </div>
      </div>
    );
  }

  const handleAddToCart = (product) => {
    const qty = quantities[product.id] || 1;
    for (let i = 0; i < qty; i++) addToCart(product);
    setAddedToCart(prev => ({ ...prev, [product.id]: true }));
    setTimeout(() => setAddedToCart(prev => ({ ...prev, [product.id]: false })), 1500);
  };

  const updateQuantity = (productId, delta) => {
    setQuantities(prev => ({
      ...prev,
      [productId]: Math.max(1, (prev[productId] || 1) + delta),
    }));
  };

  const toggleWishlist = (productId) => {
    setWishlist(prev => ({ ...prev, [productId]: !prev[productId] }));
  };

  const cartItemCount = cart.reduce((sum, item) => sum + item.quantity, 0);
  const cartTotal = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

  const primaryColor = store.primaryColor || '#9333ea';
  const secondaryColor = store.secondaryColor || '#a855f7';

  return (
    <div className="min-h-screen bg-gray-50">

      {/* ── Store Header ── */}
      <div style={{ background: `linear-gradient(135deg, ${primaryColor}, ${secondaryColor})` }}>
        <div className="px-4 pt-4 pb-6">
          {/* Back button */}
          <button
            onClick={() => navigate(-1)}
            className="flex items-center text-white/80 hover:text-white mb-4 text-sm"
          >
            <ArrowLeft className="w-4 h-4 mr-1" />
            Back
          </button>

          {/* Store identity */}
          <div className="flex items-center space-x-3">
            <div className="flex-shrink-0">
              {storeLogo ? (
                <img
                  src={storeLogo}
                  alt={store.name}
                  className="w-16 h-16 object-cover rounded-2xl border-2 border-white/30 shadow-lg"
                />
              ) : (
                <div className="w-16 h-16 bg-white/20 rounded-2xl flex items-center justify-center border-2 border-white/30">
                  <Store className="w-8 h-8 text-white" />
                </div>
              )}
            </div>
            <div className="flex-1 min-w-0">
              <h1 className="text-xl font-bold text-white truncate">{store.name}</h1>
              <p className="text-white/80 text-sm truncate">{store.description}</p>
              <div className="flex items-center flex-wrap gap-2 mt-1">
                <span className="bg-white/20 text-white text-xs px-2 py-0.5 rounded-full">
                  {store.category}
                </span>
                <span className="text-white/80 text-xs">
                  {storeProducts.length} products
                </span>
                {isStoreOwner && (
                  <span className="bg-yellow-400 text-yellow-900 text-xs px-2 py-0.5 rounded-full font-semibold">
                    Your Store
                  </span>
                )}
              </div>
            </div>
          </div>

          {/* Store stats row */}
          <div className="flex items-center justify-around mt-4 bg-white/10 rounded-2xl py-3">
            <div className="text-center">
              <p className="text-white font-bold text-lg">{storeProducts.length}</p>
              <p className="text-white/70 text-xs">Products</p>
            </div>
            <div className="w-px h-8 bg-white/20" />
            <div className="text-center">
              <div className="flex items-center justify-center space-x-1">
                {[...Array(5)].map((_, i) => (
                  <Star key={i} className="w-3 h-3 text-yellow-300 fill-yellow-300" />
                ))}
              </div>
              <p className="text-white/70 text-xs">4.8 Rating</p>
            </div>
            <div className="w-px h-8 bg-white/20" />
            <div className="text-center">
              <p className="text-white font-bold text-lg">98%</p>
              <p className="text-white/70 text-xs">Positive</p>
            </div>
          </div>
        </div>
      </div>

      {/* ── Owner Action Bar ── */}
      {isStoreOwner && (
        <div className="bg-amber-50 border-b border-amber-200 px-4 py-3 flex items-center justify-between">
          <div className="flex items-center space-x-2">
            <div className="w-2 h-2 rounded-full bg-amber-500 animate-pulse" />
            <p className="text-sm font-medium text-amber-800">Store Owner View</p>
          </div>
          <div className="flex space-x-2">
            <button
              onClick={copyStoreLink}
              className="flex items-center space-x-1 bg-white border border-amber-300 text-amber-700 px-3 py-1.5 rounded-lg text-xs font-medium hover:bg-amber-50"
            >
              <Copy className="w-3 h-3" />
              <span>Copy Link</span>
            </button>
            <button
              onClick={() => navigate('/manage-products')}
              className="flex items-center space-x-1 bg-amber-600 text-white px-3 py-1.5 rounded-lg text-xs font-medium hover:bg-amber-700"
            >
              <Edit className="w-3 h-3" />
              <span>Edit Store</span>
            </button>
          </div>
        </div>
      )}

      {/* ── Buyer Trust Bar ── */}
      {!isStoreOwner && (
        <div className="bg-white border-b border-gray-100 px-4 py-2.5">
          <div className="flex items-center justify-around text-xs text-gray-600">
            <div className="flex items-center space-x-1">
              <Shield className="w-3.5 h-3.5 text-green-600" />
              <span className="font-medium">Buyer Protected</span>
            </div>
            <div className="w-px h-4 bg-gray-200" />
            <div className="flex items-center space-x-1">
              <Truck className="w-3.5 h-3.5 text-blue-600" />
              <span className="font-medium">Fast Delivery</span>
            </div>
            <div className="w-px h-4 bg-gray-200" />
            <div className="flex items-center space-x-1">
              <Zap className="w-3.5 h-3.5 text-purple-600" />
              <span className="font-medium">Secure Pay</span>
            </div>
          </div>
        </div>
      )}

      {/* ── Sticky Cart Bar ── */}
      {canBuy && cartItemCount > 0 && (
        <div className="sticky top-0 z-30 bg-white shadow-md px-4 py-2.5 border-b border-gray-100">
          <button
            onClick={() => navigate('/checkout')}
            className="w-full text-white py-3 px-4 rounded-xl font-bold text-sm flex items-center justify-between shadow-lg"
            style={{ background: `linear-gradient(135deg, ${primaryColor}, ${secondaryColor})` }}
          >
            <div className="flex items-center space-x-2">
              <ShoppingCart className="w-5 h-5" />
              <span>View Cart</span>
              <span className="bg-white/25 text-white text-xs font-bold px-2 py-0.5 rounded-full">
                {cartItemCount}
              </span>
            </div>
            <div className="flex items-center space-x-1">
              <span className="font-bold">${cartTotal.toFixed(2)}</span>
              <ChevronRight className="w-4 h-4" />
            </div>
          </button>
        </div>
      )}

      {/* ── Products Section ── */}
      <div className="px-3 pt-4 pb-24">
        {storeProducts.length === 0 ? (
          <div className="text-center py-16">
            <div className="w-20 h-20 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-4">
              <Package className="w-10 h-10 text-gray-300" />
            </div>
            <p className="text-gray-500 font-medium">No products available yet</p>
            <p className="text-gray-400 text-sm mt-1">Check back soon!</p>
            {isStoreOwner && (
              <button
                onClick={() => navigate('/manage-products')}
                className="mt-4 bg-primary text-white px-6 py-2.5 rounded-xl text-sm font-semibold hover:opacity-90 transition"
              >
                Add Products
              </button>
            )}
          </div>
        ) : (
          <>
            <div className="flex items-center justify-between mb-3">
              <h2 className="text-base font-bold text-gray-900">
                Products <span className="text-gray-400 font-normal text-sm">({storeProducts.length})</span>
              </h2>
            </div>

            {/* 2-column product grid */}
            <div className="grid grid-cols-2 gap-3">
              {storeProducts.map(product => {
                const inStock = product.stock === undefined || product.stock > 0;
                const lowStock = product.stock !== undefined && product.stock > 0 && product.stock < 5;
                const qty = quantities[product.id] || 1;
                const isAdded = addedToCart[product.id];
                const isWished = wishlist[product.id];

                return (
                  <div
                    key={product.id}
                    className="bg-white rounded-2xl overflow-hidden shadow-sm border border-gray-100 flex flex-col"
                    style={{ boxShadow: '0 2px 12px rgba(0,0,0,0.06)' }}
                  >
                    {/* Product Image */}
                    <div className="relative bg-gray-50 aspect-square">
                      {product.images && product.images.length > 0 ? (
                        <img
                          src={product.images[0]}
                          alt={product.name}
                          className="w-full h-full object-cover"
                        />
                      ) : (
                        <div className="w-full h-full flex items-center justify-center">
                          <Package className="w-12 h-12 text-gray-200" />
                        </div>
                      )}

                      {/* Wishlist button */}
                      <button
                        onClick={() => toggleWishlist(product.id)}
                        className="absolute top-2 right-2 w-7 h-7 bg-white rounded-full shadow flex items-center justify-center"
                      >
                        <Heart
                          className={`w-3.5 h-3.5 ${isWished ? 'text-red-500 fill-red-500' : 'text-gray-400'}`}
                        />
                      </button>

                      {/* Badges */}
                      <div className="absolute top-2 left-2 flex flex-col gap-1">
                        {!inStock && (
                          <span className="bg-red-500 text-white text-xs px-1.5 py-0.5 rounded font-medium">
                            Out of Stock
                          </span>
                        )}
                        {lowStock && (
                          <span className="bg-orange-500 text-white text-xs px-1.5 py-0.5 rounded font-medium">
                            Only {product.stock} left
                          </span>
                        )}
                      </div>
                    </div>

                    {/* Product Info */}
                    <div className="p-2.5 flex flex-col flex-1">
                      {/* Name */}
                      <h3 className="text-sm font-semibold text-gray-900 line-clamp-2 leading-tight mb-1">
                        {product.name}
                      </h3>

                      {/* Stars */}
                      <div className="flex items-center space-x-1 mb-1.5">
                        <div className="flex">
                          {[...Array(5)].map((_, i) => (
                            <Star key={i} className="w-3 h-3 text-yellow-400 fill-yellow-400" />
                          ))}
                        </div>
                        <span className="text-gray-400 text-xs">(42)</span>
                      </div>

                      {/* Price */}
                      <div className="mb-2">
                        <span
                          className="text-lg font-bold"
                          style={{ color: primaryColor }}
                        >
                          ${Number(product.price || 0).toFixed(2)}
                        </span>
                      </div>

                      {/* Free shipping badge */}
                      <div className="flex items-center space-x-1 mb-2.5">
                        <Tag className="w-3 h-3 text-green-600" />
                        <span className="text-green-600 text-xs font-medium">Free Shipping</span>
                      </div>

                      {/* Quantity + Add to Cart */}
                      <div className="mt-auto">
                        {canBuy && inStock ? (
                          <>
                            {/* Quantity selector */}
                            <div className="flex items-center justify-between border border-gray-200 rounded-lg mb-2 overflow-hidden">
                              <button
                                onClick={() => updateQuantity(product.id, -1)}
                                className="w-8 h-8 flex items-center justify-center hover:bg-gray-50 text-gray-600"
                              >
                                <Minus className="w-3 h-3" />
                              </button>
                              <span className="text-sm font-semibold text-gray-900 flex-1 text-center">
                                {qty}
                              </span>
                              <button
                                onClick={() => updateQuantity(product.id, 1)}
                                className="w-8 h-8 flex items-center justify-center hover:bg-gray-50 text-gray-600"
                              >
                                <Plus className="w-3 h-3" />
                              </button>
                            </div>

                            {/* Add to Cart button */}
                            <button
                              onClick={() => handleAddToCart(product)}
                              className="w-full py-2 rounded-xl text-white text-xs font-bold flex items-center justify-center space-x-1.5 transition-all active:scale-95"
                              style={{
                                background: isAdded
                                  ? 'linear-gradient(135deg, #10b981, #059669)'
                                  : `linear-gradient(135deg, ${primaryColor}, ${secondaryColor})`,
                              }}
                            >
                              {isAdded ? (
                                <>
                                  <CheckCircle className="w-3.5 h-3.5" />
                                  <span>Added!</span>
                                </>
                              ) : (
                                <>
                                  <ShoppingCart className="w-3.5 h-3.5" />
                                  <span>Add to Cart</span>
                                </>
                              )}
                            </button>
                          </>
                        ) : isStoreOwner ? (
                          <div className="w-full py-2 rounded-xl bg-gray-100 text-gray-500 text-xs font-medium text-center">
                            Your Product
                          </div>
                        ) : (
                          <button
                            disabled
                            className="w-full py-2 rounded-xl bg-gray-200 text-gray-400 text-xs font-medium"
                          >
                            Out of Stock
                          </button>
                        )}
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          </>
        )}
      </div>
    </div>
  );
}
