import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useStore } from '../contexts/StoreContext';
import { Zap, Store, ShoppingCart, User, Plus, LogOut, LayoutDashboard } from 'lucide-react';

export default function Navigation() {
  const { currentUser, userType, logout } = useAuth();
  const { cart } = useStore();
  const location = useLocation();

  const cartItemCount = cart.reduce((sum, item) => sum + item.quantity, 0);

  // Don't show navigation on auth pages
  if (['/login', '/register'].includes(location.pathname)) {
    return null;
  }

  return (
    <nav className="fixed bottom-0 left-0 right-0 z-50 flex justify-center pointer-events-none pb-8 px-4">
      <div className="bg-white/90 backdrop-blur-xl border border-gray-100 rounded-full px-2 py-2 w-full max-w-[340px] flex justify-between items-center pointer-events-auto shadow-2xl shadow-purple-900/10">

        {/* Left Tab: Browse/Inventory */}
        <Link
          to={userType === 'seller' ? '/manage-products' : '/explore'}
          className={`flex flex-col items-center py-2 px-6 transition-all rounded-full ${location.pathname === '/explore' || location.pathname === '/manage-products' ? 'text-primary' : 'text-gray-400 hover:text-gray-600'}`}
        >
          {userType === 'seller' ? <Plus className="w-5 h-5" /> : <Store className="w-5 h-5" />}
          <span className="text-[9px] font-extrabold uppercase mt-1 tracking-tighter">
            {userType === 'seller' ? 'Add' : 'Shop'}
          </span>
        </Link>

        {/* Center: THE ZAP HUB (Home & Account) */}
        <Link
          to="/"
          className="relative -mt-10"
        >
          <div className="absolute inset-0 bg-primary/20 rounded-full blur-xl animate-pulse"></div>
          <div className={`relative w-16 h-16 rounded-full flex flex-col items-center justify-center transition-all shadow-xl ${location.pathname === '/' ? 'bg-primary text-white scale-110 shadow-primary/40' : 'bg-gray-900 text-white'}`}>
            <Zap className="w-6 h-6 fill-current" />
            <span className="text-[8px] font-black uppercase tracking-tight">Zaphub</span>
          </div>
        </Link>

        {/* Right Tab: Cart/Admin */}
        <Link
          to={currentUser ? (userType === 'seller' ? '/seller-dashboard' : '/checkout') : '/login'}
          className={`flex flex-col items-center py-2 px-6 transition-all rounded-full ${location.pathname.includes('dashboard') || location.pathname === '/checkout' || location.pathname === '/login' ? 'text-primary' : 'text-gray-400 hover:text-gray-600'}`}
        >
          {userType === 'seller' ? <LayoutDashboard className="w-5 h-5" /> : (
            <div className="relative">
              <ShoppingCart className="w-5 h-5" />
              {cartItemCount > 0 && <span className="absolute -top-1 -right-1 w-2.5 h-2.5 bg-red-500 rounded-full border border-white" />}
            </div>
          )}
          <span className="text-[9px] font-extrabold uppercase mt-1 tracking-tighter">
            {currentUser ? (userType === 'seller' ? 'Admin' : 'Cart') : 'Login'}
          </span>
        </Link>
      </div>
    </nav>
  );
}
