import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useStore } from '../contexts/StoreContext';
import { ShoppingCart, Plus, Minus, ArrowLeft, Store, Package, Edit, Video, Play, Copy } from 'lucide-react';

export default function StoreView() {
  const { storeId } = useParams();
  const { stores, products, addToCart, cart } = useStore();
  const { currentUser, userType } = useAuth();
  const navigate = useNavigate();

  const store = stores.find(s => s.id === storeId);
  const storeProducts = products.filter(p => p.storeId === storeId);
  const [quantities, setQuantities] = useState({});
  const [selectedProduct, setSelectedProduct] = useState(null);

  // Check if current user is the owner of this store
  const isStoreOwner = store && currentUser && store.ownerId === currentUser.uid;
  const canBuy = !isStoreOwner && userType === 'buyer';

  const copyStoreLink = () => {
    const link = `${window.location.origin}/store/${storeId}`;
    navigator.clipboard.writeText(link);
    alert('Store link copied to clipboard!');
  };

  if (!store) {
    return (
      <div className="min-h-screen bg-gray-50 p-4">
        <div className="text-center py-12">
          <Store className="w-16 h-16 text-gray-300 mx-auto mb-4" />
          <h2 className="text-xl font-semibold text-gray-900 mb-2">Store Not Found</h2>
          <p className="text-gray-600 mb-6">This store doesn't exist or has been removed</p>
          <button
            onClick={() => navigate('/')}
            className="bg-primary text-white px-6 py-2 rounded-lg font-medium hover:bg-blue-600 transition-colors"
          >
            Go Home
          </button>
        </div>
      </div>
    );
  }

  const handleAddToCart = (product) => {
    const quantity = quantities[product.id] || 1;
    for (let i = 0; i < quantity; i++) {
      addToCart(product);
    }
    setQuantities({...quantities, [product.id]: 1});
  };

  const updateQuantity = (productId, change) => {
    setQuantities(prev => ({
      ...prev,
      [productId]: Math.max(1, (prev[productId] || 1) + change)
    }));
  };

  const cartItemCount = cart.reduce((sum, item) => sum + item.quantity, 0);

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Store Header */}
      <div 
        className="text-white p-6"
        style={{ backgroundColor: store.primaryColor || '#3b82f6' }}
      >
        <button
          onClick={() => navigate(-1)}
          className="flex items-center text-white/80 hover:text-white mb-4"
        >
          <ArrowLeft className="w-4 h-4 mr-2" />
          Back
        </button>

        <div className="text-center">
          <div className="flex items-center justify-center mb-3">
            {store.logo ? (
              <img
                src={store.logo}
                alt={store.name}
                className="w-16 h-16 object-cover rounded-lg border-2 border-white/30"
              />
            ) : (
              <Store className="w-12 h-12" />
            )}
          </div>
          <h1 className="text-2xl font-bold mb-2">{store.name}</h1>
          <p className="text-white/90 mb-3">{store.description}</p>
          <div className="flex justify-center items-center space-x-4 text-sm">
            <span className="bg-white/20 px-3 py-1 rounded-full">
              {store.category}
            </span>
            <span className="text-white/80">
              {storeProducts.length} products
            </span>
          </div>

          {isStoreOwner && (
            <div className="mt-4">
              <span className="bg-yellow-400 text-yellow-900 px-3 py-1 rounded-full text-xs font-medium">
                Your Store
              </span>
            </div>
          )}
        </div>
      </div>

      {/* Copy Store Link */}
      <div className="bg-white border-b border-gray-200 p-4">
        <div className="flex items-center justify-between">
          <div>
            <p className="text-sm font-medium text-gray-900">Share this store</p>
            <p className="text-xs text-gray-500">Copy link to share with customers</p>
          </div>
          <button
            onClick={copyStoreLink}
            className="text-white p-2 rounded-lg hover:opacity-90 transition-opacity flex items-center"
            style={{ backgroundColor: store.secondaryColor || '#10b981' }}
          >
            <Copy className="w-4 h-4 mr-1" />
            Copy Link
          </button>
        </div>
      </div>

      {/* Owner Actions */}
      {isStoreOwner && (
        <div className="bg-yellow-50 border-b border-yellow-200 p-4">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-yellow-800">Store Owner View</p>
              <p className="text-xs text-yellow-600">You're viewing your own store</p>
            </div>
            <button
              onClick={() => navigate('/manage-products')}
              className="bg-yellow-600 text-white px-3 py-1 rounded-lg text-sm font-medium hover:bg-yellow-700 transition-colors flex items-center"
            >
              <Edit className="w-4 h-4 mr-1" />
              Manage Products
            </button>
          </div>
        </div>
      )}

      {/* Cart Button for Buyers */}
      {canBuy && cartItemCount > 0 && (
        <div className="sticky top-0 z-10 bg-white border-b border-gray-200 p-4">
          <button
            onClick={() => navigate('/checkout')}
            className="w-full bg-secondary text-white py-3 px-4 rounded-lg font-medium hover:bg-green-600 transition-colors flex items-center justify-center"
          >
            <ShoppingCart className="w-5 h-5 mr-2" />
            View Cart ({cartItemCount} items)
          </button>
        </div>
      )}

      {/* Products */}
      <div className="p-4">
        <h2 className="text-lg font-semibold text-gray-900 mb-4">Products</h2>

        {storeProducts.length === 0 ? (
          <div className="text-center py-12">
            <Package className="w-12 h-12 text-gray-300 mx-auto mb-3" />
            <p className="text-gray-500">No products available yet</p>
            {isStoreOwner && (
              <button
                onClick={() => navigate('/manage-products')}
                className="mt-4 bg-primary text-white px-4 py-2 rounded-lg text-sm font-medium hover:bg-blue-600 transition-colors"
              >
                Add Products
              </button>
            )}
          </div>
        ) : (
          <div className="grid grid-cols-1 gap-4">
            {storeProducts.map(product => (
              <div key={product.id} className="bg-white rounded-lg shadow-sm overflow-hidden">
                <div className="flex">
                  {/* Product Images */}
                  <div className="flex-shrink-0">
                    {product.images && product.images.length > 0 ? (
                      <div className="relative">
                        <img
                          src={product.images[0]}
                          alt={product.name}
                          className="w-32 h-32 object-cover"
                        />
                        {product.images.length > 1 && (
                          <div className="absolute bottom-2 right-2 bg-black/50 text-white text-xs px-2 py-1 rounded">
                            +{product.images.length - 1}
                          </div>
                        )}
                        {product.video && (
                          <div className="absolute top-2 right-2 bg-red-500 text-white p-1 rounded-full">
                            <Video className="w-3 h-3" />
                          </div>
                        )}
                      </div>
                    ) : (
                      <div className="w-32 h-32 bg-gray-200 flex items-center justify-center">
                        <Package className="w-8 h-8 text-gray-400" />
                      </div>
                    )}
                  </div>

                  <div className="flex-1 p-4">
                    <h3 className="font-medium text-gray-900 mb-1">{product.name}</h3>
                    <p className="text-sm text-gray-600 mb-2 line-clamp-2">
                      {product.description || 'No description available'}
                    </p>

                    {/* Media indicators */}
                    <div className="flex items-center space-x-2 mb-2">
                      {product.images && product.images.length > 0 && (
                        <span className="text-xs text-gray-500 flex items-center">
                          <Package className="w-3 h-3 mr-1" />
                          {product.images.length} photo{product.images.length > 1 ? 's' : ''}
                        </span>
                      )}
                      {product.video && (
                        <span className="text-xs text-gray-500 flex items-center">
                          <Video className="w-3 h-3 mr-1" />
                          Video
                        </span>
                      )}
                    </div>

                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-lg font-bold text-primary">${product.price}</p>
                        {product.stock !== undefined && product.stock > 0 && (
                          <p className="text-xs text-gray-500">
                            {product.stock} in stock
                          </p>
                        )}
                        {product.stock === 0 && (
                          <p className="text-xs text-red-600">Out of stock</p>
                        )}
                      </div>

                      {canBuy && (product.stock === undefined || product.stock > 0) ? (
                        <div className="flex items-center space-x-2">
                          <div className="flex items-center border border-gray-300 rounded-lg">
                            <button
                              onClick={() => updateQuantity(product.id, -1)}
                              className="p-2 hover:bg-gray-100 transition-colors"
                            >
                              <Minus className="w-4 h-4" />
                            </button>
                            <span className="px-3 py-1 font-medium">
                              {quantities[product.id] || 1}
                            </span>
                            <button
                              onClick={() => updateQuantity(product.id, 1)}
                              className="p-2 hover:bg-gray-100 transition-colors"
                            >
                              <Plus className="w-4 h-4" />
                            </button>
                          </div>

                          <button
                            onClick={() => handleAddToCart(product)}
                            className="bg-primary text-white p-2 rounded-lg hover:bg-blue-600 transition-colors"
                          >
                            <ShoppingCart className="w-4 h-4" />
                          </button>
                        </div>
                      ) : isStoreOwner ? (
                        <div className="text-right">
                          <span className="bg-gray-100 text-gray-600 px-3 py-1 rounded-lg text-sm">
                            Your Product
                          </span>
                        </div>
                      ) : (
                        <button
                          disabled
                          className="bg-gray-100 text-gray-400 px-4 py-2 rounded-lg text-sm font-medium cursor-not-allowed"
                        >
                          {product.stock === 0 ? 'Out of Stock' : 'Login to Buy'}
                        </button>
                      )}
                    </div>
                  </div>
                </div>

                {/* Product Video Preview */}
                {product.video && (
                  <div className="border-t border-gray-100 p-4">
                    <div className="flex items-center justify-between mb-2">
                      <span className="text-sm font-medium text-gray-700">Product Video</span>
                      <button
                        onClick={() => setSelectedProduct(selectedProduct === product.id ? null : product.id)}
                        className="text-primary hover:bg-blue-50 p-1 rounded transition-colors"
                      >
                        {selectedProduct === product.id ? 'Hide' : <Play className="w-4 h-4" />}
                      </button>
                    </div>

                    {selectedProduct === product.id && (
                      <div className="space-y-2">
                        <video
                          src={product.video}
                          controls
                          className="w-full h-48 object-cover rounded-lg"
                        />
                        <p className="text-xs text-gray-500">Product video demonstration</p>
                      </div>
                    )}
                  </div>
                )}

                {/* Product Images Gallery */}
                {product.images && product.images.length > 1 && (
                  <div className="border-t border-gray-100 p-4">
                    <p className="text-sm font-medium text-gray-700 mb-2">More Images</p>
                    <div className="grid grid-cols-4 gap-2">
                      {product.images.slice(1).map((image, index) => (
                        <img
                          key={index}
                          src={image}
                          alt={`${product.name} ${index + 2}`}
                          className="w-full h-20 object-cover rounded cursor-pointer hover:opacity-80 transition-opacity"
                        />
                      ))}
                    </div>
                  </div>
                )}
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
