import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useStore } from '../contexts/StoreContext';
import { ShoppingCart, Plus, Minus, ArrowLeft, Store, Package } from 'lucide-react';

export default function StoreView() {
  const { storeId } = useParams();
  const { stores, products, addToCart, cart } = useStore();
  const navigate = useNavigate();

  const store = stores.find(s => s.id === storeId);
  const storeProducts = products.filter(p => p.storeId === storeId);

  const [quantities, setQuantities] = useState({});

  if (!store) {
    return (
      <div className="min-h-screen bg-gray-50 p-4">
        <div className="text-center py-12">
          <Store className="w-16 h-16 text-gray-300 mx-auto mb-4" />
          <h2 className="text-xl font-semibold text-gray-900 mb-2">Store Not Found</h2>
          <p className="text-gray-600 mb-6">This store doesn't exist or has been removed</p>
          <button
            onClick={() => navigate('/')}
            className="bg-primary text-white px-6 py-2 rounded-lg font-medium hover:bg-blue-600 transition-colors"
          >
            Go Home
          </button>
        </div>
      </div>
    );
  }

  const handleAddToCart = (product) => {
    const quantity = quantities[product.id] || 1;
    for (let i = 0; i < quantity; i++) {
      addToCart(product);
    }
    setQuantities({...quantities, [product.id]: 1});
  };

  const updateQuantity = (productId, change) => {
    setQuantities(prev => ({
      ...prev,
      [productId]: Math.max(1, (prev[productId] || 1) + change)
    }));
  };

  const cartItemCount = cart.reduce((sum, item) => sum + item.quantity, 0);

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Store Header */}
      <div className="bg-primary text-white p-6">
        <button
          onClick={() => navigate(-1)}
          className="flex items-center text-white/80 hover:text-white mb-4"
        >
          <ArrowLeft className="w-4 h-4 mr-2" />
          Back
        </button>

        <div className="text-center">
          <Store className="w-12 h-12 mx-auto mb-3" />
          <h1 className="text-2xl font-bold mb-2">{store.name}</h1>
          <p className="text-white/90 mb-3">{store.description}</p>
          <div className="flex justify-center items-center space-x-4 text-sm">
            <span className="bg-white/20 px-3 py-1 rounded-full">
              {store.category}
            </span>
            <span className="text-white/80">
              {storeProducts.length} products
            </span>
          </div>
        </div>
      </div>

      {/* Cart Button */}
      {cartItemCount > 0 && (
        <div className="sticky top-0 z-10 bg-white border-b border-gray-200 p-4">
          <button
            onClick={() => navigate('/checkout')}
            className="w-full bg-secondary text-white py-3 px-4 rounded-lg font-medium hover:bg-green-600 transition-colors flex items-center justify-center"
          >
            <ShoppingCart className="w-5 h-5 mr-2" />
            View Cart ({cartItemCount} items)
          </button>
        </div>
      )}

      {/* Products */}
      <div className="p-4">
        <h2 className="text-lg font-semibold text-gray-900 mb-4">Products</h2>

        {storeProducts.length === 0 ? (
          <div className="text-center py-12">
            <Package className="w-12 h-12 text-gray-300 mx-auto mb-3" />
            <p className="text-gray-500">No products available yet</p>
          </div>
        ) : (
          <div className="grid grid-cols-1 gap-4">
            {storeProducts.map(product => (
              <div key={product.id} className="bg-white rounded-lg shadow-sm p-4">
                <div className="flex space-x-4">
                  <div className="w-20 h-20 bg-gray-200 rounded-lg flex items-center justify-center flex-shrink-0">
                    <Package className="w-8 h-8 text-gray-400" />
                  </div>

                  <div className="flex-1">
                    <h3 className="font-medium text-gray-900 mb-1">{product.name}</h3>
                    <p className="text-sm text-gray-600 mb-2 line-clamp-2">
                      {product.description || 'No description available'}
                    </p>

                    <div className="flex items-center justify-between">
                      <div>
                        <p className="text-lg font-bold text-primary">${product.price}</p>
                        {product.stock !== undefined && product.stock > 0 && (
                          <p className="text-xs text-gray-500">
                            {product.stock} in stock
                          </p>
                        )}
                        {product.stock === 0 && (
                          <p className="text-xs text-red-600">Out of stock</p>
                        )}
                      </div>

                      {product.stock === undefined || product.stock > 0 ? (
                        <div className="flex items-center space-x-2">
                          <div className="flex items-center border border-gray-300 rounded-lg">
                            <button
                              onClick={() => updateQuantity(product.id, -1)}
                              className="p-2 hover:bg-gray-100 transition-colors"
                            >
                              <Minus className="w-4 h-4" />
                            </button>
                            <span className="px-3 py-1 font-medium">
                              {quantities[product.id] || 1}
                            </span>
                            <button
                              onClick={() => updateQuantity(product.id, 1)}
                              className="p-2 hover:bg-gray-100 transition-colors"
                            >
                              <Plus className="w-4 h-4" />
                            </button>
                          </div>

                          <button
                            onClick={() => handleAddToCart(product)}
                            className="bg-primary text-white p-2 rounded-lg hover:bg-blue-600 transition-colors"
                          >
                            <ShoppingCart className="w-4 h-4" />
                          </button>
                        </div>
                      ) : (
                        <button
                          disabled
                          className="bg-gray-100 text-gray-400 px-4 py-2 rounded-lg text-sm font-medium cursor-not-allowed"
                        >
                          Out of Stock
                        </button>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
