import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useStore } from '../contexts/StoreContext';
import { Store, ArrowLeft } from 'lucide-react';

export default function CreateStore() {
  const [storeName, setStoreName] = useState('');
  const [description, setDescription] = useState('');
  const [category, setCategory] = useState('');
  const [loading, setLoading] = useState(false);
  const { currentUser } = useAuth();
  const { addStore } = useStore();
  const navigate = useNavigate();

  const categories = [
    'Electronics',
    'Clothing & Fashion',
    'Food & Beverages',
    'Home & Garden',
    'Health & Beauty',
    'Sports & Outdoors',
    'Books & Media',
    'Toys & Games',
    'Automotive',
    'Other'
  ];

  async function handleSubmit(e) {
    e.preventDefault();

    if (!storeName || !description || !category) {
      return alert('Please fill in all fields');
    }

    setLoading(true);

    try {
      const newStore = {
        name: storeName,
        description,
        category,
        ownerId: currentUser.uid,
        ownerEmail: currentUser.email,
        createdAt: new Date().toISOString(),
        isActive: true
      };

      addStore(newStore);
      navigate('/seller-dashboard');
    } catch (error) {
      console.error('Error creating store:', error);
      alert('Failed to create store. Please try again.');
    }

    setLoading(false);
  }

  return (
    <div className="min-h-screen bg-gray-50 p-4">
      <div className="mb-6">
        <button
          onClick={() => navigate('/seller-dashboard')}
          className="flex items-center text-gray-600 hover:text-gray-900 mb-4"
        >
          <ArrowLeft className="w-4 h-4 mr-2" />
          Back to Dashboard
        </button>

        <div className="text-center">
          <Store className="w-12 h-12 text-primary mx-auto mb-3" />
          <h1 className="text-2xl font-bold text-gray-900 mb-2">Create Your Store</h1>
          <p className="text-gray-600">Set up your online store in minutes</p>
        </div>
      </div>

      <div className="bg-white rounded-lg shadow-sm p-6">
        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Store Name *
            </label>
            <input
              type="text"
              value={storeName}
              onChange={(e) => setStoreName(e.target.value)}
              required
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary"
              placeholder="Enter your store name"
            />
            <p className="text-xs text-gray-500 mt-1">
              This will be visible to customers
            </p>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Store Description *
            </label>
            <textarea
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              required
              rows={4}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary"
              placeholder="Describe what your store offers..."
            />
            <p className="text-xs text-gray-500 mt-1">
              Tell customers about your store and what makes it special
            </p>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Category *
            </label>
            <select
              value={category}
              onChange={(e) => setCategory(e.target.value)}
              required
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary"
            >
              <option value="">Select a category</option>
              {categories.map(cat => (
                <option key={cat} value={cat}>{cat}</option>
              ))}
            </select>
          </div>

          <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
            <h3 className="font-medium text-blue-900 mb-2">What happens next?</h3>
            <ul className="text-sm text-blue-800 space-y-1">
              <li>• Your store will be created immediately</li>
              <li>• You'll get a unique store link to share</li>
              <li>• You can start adding products right away</li>
              <li>• Customers can browse and place orders</li>
            </ul>
          </div>

          <button
            type="submit"
            disabled={loading}
            className="w-full bg-primary text-white py-3 px-4 rounded-lg font-medium hover:bg-blue-600 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {loading ? 'Creating Store...' : 'Create Store'}
          </button>
        </form>
      </div>
    </div>
  );
}
