import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useStore } from '../contexts/StoreContext';
import { ShoppingCart, Package, Clock, CheckCircle, Search, Store, ExternalLink } from 'lucide-react';

export default function BuyerDashboard() {
  const { currentUser } = useAuth();
  const { orders, cart, stores } = useStore();

  const myOrders = orders.filter(order => order.customerId === currentUser?.uid);

  // Group orders by store
  const ordersByStore = myOrders.reduce((acc, order) => {
    if (!acc[order.storeId]) {
      acc[order.storeId] = {
        store: stores.find(s => s.id === order.storeId),
        orders: []
      };
    }
    acc[order.storeId].orders.push(order);
    return acc;
  }, {});

  const orderStats = {
    pending: myOrders.filter(order => order.status === 'pending').length,
    processing: myOrders.filter(order => order.status === 'processing').length,
    completed: myOrders.filter(order => order.status === 'completed').length,
    total: myOrders.reduce((sum, order) => sum + order.total, 0)
  };

  return (
    <div className="min-h-screen bg-gray-50 p-4">
      <div className="mb-6">
        <h1 className="text-2xl font-bold text-gray-900 mb-2">Buyer Dashboard</h1>
        <p className="text-gray-600">Track your orders and shopping activity</p>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-2 gap-4 mb-6">
        <div className="bg-white rounded-lg p-4 shadow-sm">
          <div className="flex items-center justify-between mb-2">
            <ShoppingCart className="w-5 h-5 text-primary" />
            <span className="text-xs text-gray-500">Items</span>
          </div>
          <p className="text-2xl font-bold text-gray-900">{cart.length}</p>
          <p className="text-xs text-gray-600">In Cart</p>
        </div>

        <div className="bg-white rounded-lg p-4 shadow-sm">
          <div className="flex items-center justify-between mb-2">
            <Package className="w-5 h-5 text-secondary" />
            <span className="text-xs text-gray-500">Total</span>
          </div>
          <p className="text-2xl font-bold text-gray-900">{myOrders.length}</p>
          <p className="text-xs text-gray-600">Orders</p>
        </div>

        <div className="bg-white rounded-lg p-4 shadow-sm">
          <div className="flex items-center justify-between mb-2">
            <Clock className="w-5 h-5 text-accent" />
            <span className="text-xs text-gray-500">Pending</span>
          </div>
          <p className="text-2xl font-bold text-gray-900">{orderStats.pending}</p>
          <p className="text-xs text-gray-600">Orders</p>
        </div>

        <div className="bg-white rounded-lg p-4 shadow-sm">
          <div className="flex items-center justify-between mb-2">
            <CheckCircle className="w-5 h-5 text-green-600" />
            <span className="text-xs text-gray-500">Spent</span>
          </div>
          <p className="text-2xl font-bold text-gray-900">${orderStats.total.toFixed(2)}</p>
          <p className="text-xs text-gray-600">Total</p>
        </div>
      </div>

      {/* Quick Actions */}
      <div className="bg-white rounded-lg p-4 shadow-sm mb-6">
        <h2 className="text-lg font-semibold text-gray-900 mb-4">Quick Actions</h2>
        <div className="grid grid-cols-2 gap-3">
          <Link
            to="/explore"
            className="bg-primary text-white p-3 rounded-lg text-center hover:bg-blue-600 transition-colors"
          >
            <Search className="w-5 h-5 mx-auto mb-1" />
            <span className="text-sm font-medium">Browse Stores</span>
          </Link>

          {cart.length > 0 && (
            <Link
              to="/checkout"
              className="bg-secondary text-white p-3 rounded-lg text-center hover:bg-green-600 transition-colors"
            >
              <ShoppingCart className="w-5 h-5 mx-auto mb-1" />
              <span className="text-sm font-medium">Checkout</span>
            </Link>
          )}
        </div>
      </div>

      {/* Cart Preview */}
      {cart.length > 0 && (
        <div className="bg-white rounded-lg p-4 shadow-sm mb-6">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-lg font-semibold text-gray-900">Shopping Cart</h2>
            <Link
              to="/checkout"
              className="text-primary text-sm font-medium hover:underline"
            >
              View All
            </Link>
          </div>

          <div className="space-y-3">
            {cart.slice(0, 3).map(item => {
              const itemStore = stores.find(s => s.id === item.storeId);
              return (
                <div key={item.id} className="flex items-center space-x-3">
                  <div className="w-12 h-12 bg-gray-200 rounded-lg flex items-center justify-center">
                    <Package className="w-6 h-6 text-gray-400" />
                  </div>
                  <div className="flex-1">
                    <p className="font-medium text-gray-900 text-sm">{item.name}</p>
                    <p className="text-xs text-gray-600">Qty: {item.quantity} Ã— ${item.price}</p>
                    {itemStore && (
                      <p className="text-xs text-primary">{itemStore.name}</p>
                    )}
                  </div>
                  <p className="font-medium text-gray-900 text-sm">
                    ${(item.price * item.quantity).toFixed(2)}
                  </p>
                </div>
              );
            })}

            {cart.length > 3 && (
              <p className="text-center text-sm text-gray-500">
                +{cart.length - 3} more items
              </p>
            )}
          </div>
        </div>
      )}

      {/* Orders by Store */}
      <div className="bg-white rounded-lg p-4 shadow-sm">
        <h2 className="text-lg font-semibold text-gray-900 mb-4">Orders by Store</h2>

        {myOrders.length === 0 ? (
          <div className="text-center py-12">
            <Package className="w-12 h-12 text-gray-300 mx-auto mb-4" />
            <p className="text-gray-500 mb-4">No orders yet</p>
            <Link
              to="/explore"
              className="bg-primary text-white px-6 py-2 rounded-lg text-sm font-medium hover:bg-blue-600 transition-colors inline-block"
            >
              Start Shopping
            </Link>
          </div>
        ) : (
          <div className="space-y-4">
            {Object.entries(ordersByStore).map(([storeId, { store, orders: storeOrders }]) => (
              <div key={storeId} className="border border-gray-200 rounded-lg p-4">
                <div className="flex justify-between items-start mb-3">
                  <div>
                    <h3 className="font-medium text-gray-900 flex items-center">
                      <Store className="w-4 h-4 mr-2 text-primary" />
                      {store?.name || 'Unknown Store'}
                    </h3>
                    <p className="text-sm text-gray-600">{storeOrders.length} orders</p>
                  </div>
                  {store && (
                    <Link
                      to={`/store/${storeId}`}
                      className="text-primary hover:bg-blue-50 p-2 rounded-lg transition-colors"
                      title="Visit Store"
                    >
                      <ExternalLink className="w-4 h-4" />
                    </Link>
                  )}
                </div>

                <div className="space-y-2">
                  {storeOrders.slice(0, 3).map(order => (
                    <div key={order.id} className="bg-gray-50 rounded-lg p-3">
                      <div className="flex justify-between items-start">
                        <div>
                          <p className="font-medium text-gray-900 text-sm">Order #{order.id.slice(-6)}</p>
                          <p className="text-xs text-gray-500">
                            {new Date(order.createdAt).toLocaleDateString()}
                          </p>
                          <p className="text-xs text-gray-600 mt-1">
                            {order.items.length} items
                          </p>
                        </div>
                        <div className="text-right">
                          <p className="font-medium text-gray-900 text-sm">${order.total.toFixed(2)}</p>
                          <span className={`text-xs px-2 py-1 rounded-full ${
                            order.status === 'pending' ? 'bg-yellow-100 text-yellow-800' :
                            order.status === 'processing' ? 'bg-blue-100 text-blue-800' :
                            order.status === 'completed' ? 'bg-green-100 text-green-800' :
                            'bg-gray-100 text-gray-800'
                          }`}>
                            {order.status}
                          </span>
                        </div>
                      </div>
                    </div>
                  ))}

                  {storeOrders.length > 3 && (
                    <p className="text-center text-xs text-gray-500">
                      +{storeOrders.length - 3} more orders from this store
                    </p>
                  )}
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
