import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { Store, ShoppingCart, User, Share2, Instagram, Facebook, MessageCircle, Plus, Search } from 'lucide-react';

export default function Home() {
  const { currentUser, userType, logout } = useAuth();

  const shareStore = (platform) => {
    const message = "Check out this amazing mobile store app! Create your own store or shop from local sellers.";
    const currentUrl = window.location.href;
    const baseUrl = currentUrl.split('/store/')[0];
    const url = baseUrl;

    let shareUrl = '';
    switch (platform) {
      case 'whatsapp':
        shareUrl = `https://wa.me/?text=${encodeURIComponent(message + ' ' + url)}`;
        break;
      case 'facebook':
        shareUrl = `https://www.facebook.com/sharer/sharer.php?u=${encodeURIComponent(url)}`;
        break;
      case 'instagram':
        // Instagram doesn't support direct sharing, so we'll copy to clipboard
        navigator.clipboard.writeText(message + ' ' + url);
        alert('Link copied! Share it on Instagram');
        return;
      default:
        return;
    }

    window.open(shareUrl, '_blank');
  };

  return (
    <div className="min-h-screen bg-white text-black p-4">
      {/* Hero Section */}
      <div className="text-center py-12">
        <div className="mb-6">
          <div className="w-20 h-20 bg-gradient-to-br from-purple-500 to-purple-700 rounded-2xl mx-auto mb-4 flex items-center justify-center">
            <Store className="w-10 h-10 text-white" />
          </div>
          <h1 className="text-3xl font-bold mb-3 text-gradient">Zapin</h1>
          <p className="text-gray-600 text-sm mb-8">Your Mobile Store Solution</p>
        </div>

        {/* User Role Status */}
        {currentUser && (
          <div className="mb-8 flex flex-col items-center">
            <div className="bg-purple-50 border border-purple-100 px-4 py-2 rounded-2xl flex items-center space-x-2 mb-2">
              <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse"></div>
              <span className="text-xs font-bold text-gray-500 uppercase tracking-widest">
                Logged in as <span className="text-primary font-extrabold">{userType?.toUpperCase() || 'USER'}</span>
              </span>
            </div>
            <button
              onClick={() => {
                if (window.confirm('Are you sure you want to log out?')) {
                  logout();
                }
              }}
              className="text-[10px] font-bold text-red-500 uppercase tracking-widest hover:underline"
            >
              Logout / Switch Account
            </button>
          </div>
        )}

        {/* Main CTA Buttons */}
        <div className="space-y-3 mb-12">
          {currentUser ? (
            <div className="grid grid-cols-1 gap-3">
              <Link
                to={userType === 'seller' ? '/seller-dashboard' : '/buyer-dashboard'}
                className="btn-primary py-4 text-center block"
              >
                {userType === 'seller' ? <Store className="w-5 h-5 inline mr-2" /> : <ShoppingCart className="w-5 h-5 inline mr-2" />}
                Go to My {userType === 'seller' ? 'Seller' : 'Buyer'} Dashboard
              </Link>

              <Link
                to="/explore"
                className="block w-full bg-gray-50 border border-gray-200 text-gray-900 py-3 px-4 rounded-xl font-bold text-sm tracking-tight hover:bg-gray-100 transition-all flex items-center justify-center"
              >
                <Search className="w-4 h-4 mr-2" />
                Browse Local Stores
              </Link>
            </div>
          ) : (
            <div className="grid grid-cols-2 gap-3">
              <Link
                to="/register"
                className="btn-primary py-4 text-center"
              >
                Sign Up
              </Link>
              <Link
                to="/login"
                className="bg-gray-100 border border-gray-200 text-black py-4 px-4 rounded-xl font-bold text-sm tracking-tight hover:bg-gray-200 transition-all text-center"
              >
                Login
              </Link>
            </div>
          )}
        </div>

        {/* Features */}
        <div className="grid grid-cols-2 gap-4 mb-12">
          <Link
            to={currentUser ? "/create-store" : "/register"}
            className="card hover:border-purple-300 transition-all text-left"
          >
            <div className="w-12 h-12 bg-purple-100 rounded-xl flex items-center justify-center mb-3">
              <Store className="w-6 h-6 text-purple-600" />
            </div>
            <h3 className="font-semibold text-black mb-1 text-sm">Create Store</h3>
            <p className="text-gray-600 text-xs">Build your mobile store in minutes</p>
          </Link>

          <Link
            to="/explore"
            className="card hover:border-purple-300 transition-all text-left"
          >
            <div className="w-12 h-12 bg-purple-100 rounded-xl flex items-center justify-center mb-3">
              <ShoppingCart className="w-6 h-6 text-purple-600" />
            </div>
            <h3 className="font-semibold text-black mb-1 text-sm">Shop Local</h3>
            <p className="text-gray-600 text-xs">Discover amazing products nearby</p>
          </Link>
        </div>

        {/* Social Sharing */}
        <div className="card">
          <h3 className="font-semibold text-black mb-4 text-sm">Share Zapin</h3>
          <div className="flex justify-center space-x-3">
            <button
              onClick={() => shareStore('whatsapp')}
              className="w-12 h-12 bg-green-100 rounded-xl flex items-center justify-center hover:bg-green-200 transition-all"
            >
              <MessageCircle className="w-5 h-5 text-green-600" />
            </button>
            <button
              onClick={() => shareStore('facebook')}
              className="w-12 h-12 bg-blue-100 rounded-xl flex items-center justify-center hover:bg-blue-200 transition-all"
            >
              <Facebook className="w-5 h-5 text-blue-600" />
            </button>
            <button
              onClick={() => shareStore('instagram')}
              className="w-12 h-12 bg-pink-100 rounded-xl flex items-center justify-center hover:bg-pink-200 transition-all"
            >
              <Instagram className="w-5 h-5 text-pink-600" />
            </button>
          </div>
        </div>

        {/* Stats */}
        <div className="mt-12 grid grid-cols-3 gap-4">
          <div className="text-center">
            <div className="text-2xl font-bold text-gradient">10K+</div>
            <div className="text-xs text-gray-600">Active Stores</div>
          </div>
          <div className="text-center">
            <div className="text-2xl font-bold text-gradient">50K+</div>
            <div className="text-xs text-gray-600">Products</div>
          </div>
          <div className="text-center">
            <div className="text-2xl font-bold text-gradient">25K+</div>
            <div className="text-xs text-gray-600">Happy Users</div>
          </div>
        </div>
      </div>
    </div>
  );
}
