import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useStore } from '../contexts/StoreContext';
import { Home, Store, ShoppingCart, User, Plus } from 'lucide-react';

export default function Navigation() {
  const { currentUser, userType, logout } = useAuth();
  const { cart } = useStore();
  const location = useLocation();

  const cartItemCount = cart.reduce((sum, item) => sum + item.quantity, 0);

  // Don't show navigation on auth pages
  if (['/login', '/register'].includes(location.pathname)) {
    return null;
  }

  return (
    <nav className="fixed bottom-0 left-0 right-0 z-50 flex justify-center pointer-events-none">
      <div className="bg-white border-t border-gray-200 px-4 py-2 w-full max-w-[440px] flex justify-around items-center pointer-events-auto shadow-lg">
        <Link
          to="/"
          className={`flex flex-col items-center p-2 transition-all ${location.pathname === '/' ? 'text-primary' : 'text-gray-400 hover:text-black'
            }`}
        >
          <Home className="w-5 h-5" />
          <span className="text-[10px] font-bold uppercase mt-1">Home</span>
        </Link>

        {/* Explore is always available for shoppers */}
        <Link
          to="/explore"
          className={`flex flex-col items-center p-2 transition-all ${location.pathname === '/explore' ? 'text-primary' : 'text-gray-400 hover:text-black'
            }`}
        >
          <Store className="w-5 h-5" />
          <span className="text-[10px] font-bold uppercase mt-1">Explore</span>
        </Link>

        {currentUser && userType === 'seller' && (
          <>
            <Link
              to="/seller-dashboard"
              className={`flex flex-col items-center p-2 transition-all ${location.pathname.includes('seller') ? 'text-primary' : 'text-gray-400 hover:text-black'
                }`}
            >
              <User className="w-5 h-5" />
              <span className="text-[10px] font-bold uppercase mt-1">Dashboard</span>
            </Link>

            <Link
              to="/create-store"
              className={`flex flex-col items-center p-2 transition-all ${location.pathname === '/create-store' ? 'text-primary' : 'text-gray-400 hover:text-black'
                }`}
            >
              <Plus className="w-5 h-5" />
              <span className="text-[10px] font-bold uppercase mt-1">New Store</span>
            </Link>
          </>
        )}

        {currentUser && userType === 'buyer' && (
          <>
            <Link
              to="/buyer-dashboard"
              className={`flex flex-col items-center p-2 transition-all ${location.pathname.includes('buyer') ? 'text-primary' : 'text-gray-400 hover:text-black'
                }`}
            >
              <ShoppingCart className="w-5 h-5" />
              <span className="text-[10px] font-bold uppercase mt-1">My Orders</span>
            </Link>

            <Link
              to="/checkout"
              className={`relative flex flex-col items-center p-2 transition-all ${location.pathname === '/checkout' ? 'text-primary' : 'text-gray-400 hover:text-black'
                }`}
            >
              <ShoppingCart className="w-5 h-5" />
              <span className="text-[10px] font-bold uppercase mt-1">Cart</span>
              {cartItemCount > 0 && (
                <span className="absolute top-1 right-2 bg-red-500 text-white text-[8px] font-bold rounded-full w-4 h-4 flex items-center justify-center border border-white">
                  {cartItemCount}
                </span>
              )}
            </Link>
          </>
        )}

        {currentUser ? (
          <button
            onClick={() => {
              if (window.confirm('Are you sure you want to log out?')) {
                logout();
              }
            }}
            className="flex flex-col items-center p-2 text-gray-400 hover:text-red-500 transition-all"
          >
            <User className="w-5 h-5" />
            <span className="text-[10px] font-bold uppercase mt-1">Logout</span>
          </button>
        ) : (
          <Link
            to="/login"
            className={`flex flex-col items-center p-2 transition-all ${location.pathname === '/login' ? 'text-primary' : 'text-gray-400 hover:text-black'
              }`}
          >
            <User className="w-5 h-5" />
            <span className="text-[10px] font-bold uppercase mt-1">Login</span>
          </Link>
        )}
      </div>
    </nav>
  );
}
